  boldRed: (message) => `\u001b[1m\u001b[31m${message}\u001b[39m\u001b[22m`,
  yellow: (message) => `\u001b[33m${message}\u001b[39m`,
  boldYellow: (message) => `\u001b[1m\u001b[33m${message}\u001b[39m\u001b[22m`,
  grey: (message) => `\u001b[90m${message}\u001b[39m`,
  boldGrey: (message) => `\u001b[1m\u001b[90m${message}\u001b[39m\u001b[22m`,
